            }
        });

        logOutButton.onClick.AddListener(() =>
        {
            if (Application.isEditor)
            {
                GoToPreviousScene();
                return;
            }

            LyncManager.Instance.walletAuth.Logout(() =>
            {
                eoaAddress.text = "";
                GoToPreviousScene();
            },
            error =>
            {
                Debug.LogError(error);
            });
        });

