    private void On721TrxCompleted(TransactionData tx)
    {
        Debug.Log("TRX DONE for ERC-721" + tx.txHash);
        send721Trx.interactable = true;
    }
    private void On1155TrxCompleted(TransactionData tx)
    {
        Debug.Log("TRX DONE for ERC-1155" + tx.txHash);
        send1155Trx.interactable = true;
    }

    private void On20TrxCompleted(TransactionData tx)
    {
        Debug.Log("TRX DONE for ERC-20" + tx.txHash);
        send20Trx.interactable = true;
    }

    private void GoToPreviousScene()
    {
        int nextSceneIndex = SceneManager.GetActiveScene().buildIndex + -1;
        Debug.Log("GoToNextScene()");
        SceneManager.LoadScene(nextSceneIndex);
    }

